mapper.delete:
delete from tb_customer

 id=#{id}
      and name=#{ name }
      and phone=#{ phone }
      and sex=#{ sex }
      and address=#{ address }
      and occupation=#{ occupation }

mapper.update:
update tb_customer

      id=#{ id },
      name=#{ name },
      phone=#{ phone },
      sex=#{ sex },
      address=#{ address },
      occupation=#{ occupation },


 id=#{id}
      and name=#{ name }
      and phone=#{ phone }
      and sex=#{ sex }
      and address=#{ address }
      and occupation=#{ occupation }

mapper.select:
select id,name,phone,sex,address,occupation from tb_customer

      and id=#{ id }
      and name=#{ name }
      and phone=#{ phone }
      and sex=#{ sex }
      and address=#{ address }
      and occupation=#{ occupation }

select id,name,phone,sex,address,occupation from tb_customer

      and id=#{ id }
      and name=#{ name }
      and phone=#{ phone }
      and sex=#{ sex }
      and address=#{ address }
      and occupation=#{ occupation }

limit #{offset},#{rows}

select id,name,phone,sex,address,occupation from tb_customer

      and id=#{ id }
      and name=#{ name }
      and phone=#{ phone }
      and sex=#{ sex }
      and address=#{ address }
      and occupation=#{ occupation }

limit 0,1

select count(1) from tb_customer

      and id = #{ id }
      and name = #{ name }
      and phone = #{ phone }
      and sex = #{ sex }
      and address = #{ address }
      and occupation = #{ occupation }
