mapper.delete:
delete from tb_discount

 id=#{id}
      and degin_time=#{ degin_time }
      and end_time=#{ end_time }
      and discount=#{ discount }

mapper.update:
update tb_discount

      id=#{ id },
      degin_time=#{ degin_time },
      end_time=#{ end_time },
      discount=#{ discount },


 id=#{id}
      and degin_time=#{ degin_time }
      and end_time=#{ end_time }
      and discount=#{ discount }

mapper.select:
select id,degin_time,end_time,discount from tb_discount

      and id=#{ id }
      and degin_time=#{ degin_time }
      and end_time=#{ end_time }
      and discount=#{ discount }

select id,degin_time,end_time,discount from tb_discount

      and id=#{ id }
      and degin_time=#{ degin_time }
      and end_time=#{ end_time }
      and discount=#{ discount }

limit #{offset},#{rows}

select id,degin_time,end_time,discount from tb_discount

      and id=#{ id }
      and degin_time=#{ degin_time }
      and end_time=#{ end_time }
      and discount=#{ discount }

limit 0,1

select count(1) from tb_discount

      and id = #{ id }
      and degin_time = #{ degin_time }
      and end_time = #{ end_time }
      and discount = #{ discount }
