mapper.delete:
delete from tb_house

 id=#{id}
      and location=#{ location }
      and building_no=#{ building_no }
      and unit_no=#{ unit_no }
      and house_no=#{ house_no }
      and house_type_id=#{ house_type_id }
      and height=#{ height }
      and area=#{ area }
      and inner_area=#{ inner_area }
      and single_price=#{ single_price }
      and delivery_time=#{ delivery_time }
      and decorate_detail=#{ decorate_detail }
      and remark=#{ remark }

mapper.update:
update tb_house
id=#{ id }, location=#{ location }, building_no=#{ building_no }, unit_no=#{ unit_no }, house_no=#{ house_no }, house_type_id=#{ house_type_id }, height=#{ height }, area=#{ area }, inner_area=#{ inner_area }, single_price=#{ single_price }, delivery_time=#{ delivery_time }, decorate_detail=#{ decorate_detail }, remark=#{ remark },
id=#{id}

mapper.select:
select id,location,building_no,unit_no,house_no,house_type_id,height,area,inner_area,single_price,delivery_time,decorate_detail,remark from tb_house

      and id=#{ id }
      and location=#{ location }
      and building_no=#{ building_no }
      and unit_no=#{ unit_no }
      and house_no=#{ house_no }
      and house_type_id=#{ house_type_id }
      and height=#{ height }
      and area=#{ area }
      and inner_area=#{ inner_area }
      and single_price=#{ single_price }
      and delivery_time=#{ delivery_time }
      and decorate_detail=#{ decorate_detail }
      and remark=#{ remark }

select id,location,building_no,unit_no,house_no,house_type_id,height,area,inner_area,single_price,delivery_time,decorate_detail,remark from tb_house

      and id=#{ id }
      and location=#{ location }
      and building_no=#{ building_no }
      and unit_no=#{ unit_no }
      and house_no=#{ house_no }
      and house_type_id=#{ house_type_id }
      and height=#{ height }
      and area=#{ area }
      and inner_area=#{ inner_area }
      and single_price=#{ single_price }
      and delivery_time=#{ delivery_time }
      and decorate_detail=#{ decorate_detail }
      and remark=#{ remark }

limit #{offset},#{rows}

select id,location,building_no,unit_no,house_no,house_type_id,height,area,inner_area,single_price,delivery_time,decorate_detail,remark from tb_house

      and id=#{ id }
      and location=#{ location }
      and building_no=#{ building_no }
      and unit_no=#{ unit_no }
      and house_no=#{ house_no }
      and house_type_id=#{ house_type_id }
      and height=#{ height }
      and area=#{ area }
      and inner_area=#{ inner_area }
      and single_price=#{ single_price }
      and delivery_time=#{ delivery_time }
      and decorate_detail=#{ decorate_detail }
      and remark=#{ remark }

limit 0,1

select count(1) from tb_house

      and id = #{ id }
      and location = #{ location }
      and building_no = #{ building_no }
      and unit_no = #{ unit_no }
      and house_no = #{ house_no }
      and house_type_id = #{ house_type_id }
      and height = #{ height }
      and area = #{ area }
      and inner_area = #{ inner_area }
