mapper.delete:
delete from tb_house_type

 id=#{id}
      and drawing_room=#{ drawing_room }
      and balcony=#{ balcony }
      and store_room=#{ store_room }
      and loft=#{ loft }
      and bedroom=#{ bedroom }
      and restroom=#{ restroom }
      and garden=#{ garden }
      and kitchen=#{ kitchen }
      and house_type=#{ house_type }
      and remark=#{ remark }
      and tb_house_id=#{ tb_house_id }

mapper.update:
update tb_house_type

      id=#{ id },
      drawing_room=#{ drawing_room },
      balcony=#{ balcony },
      store_room=#{ store_room },
      loft=#{ loft },
      bedroom=#{ bedroom },
      restroom=#{ restroom },
      garden=#{ garden },
      kitchen=#{ kitchen },
      house_type=#{ house_type },
      remark=#{ remark },
      tb_house_id=#{ tb_house_id },


 id=#{id}
      and drawing_room=#{ drawing_room }
      and balcony=#{ balcony }
      and store_room=#{ store_room }
      and loft=#{ loft }
      and bedroom=#{ bedroom }
      and restroom=#{ restroom }
      and garden=#{ garden }
      and kitchen=#{ kitchen }
      and house_type=#{ house_type }
      and remark=#{ remark }
      and tb_house_id=#{ tb_house_id }

mapper.select:
select id,drawing_room,balcony,store_room,loft,bedroom,restroom,garden,kitchen,house_type,remark,tb_house_id from tb_house_type

      and id=#{ id }
      and drawing_room=#{ drawing_room }
      and balcony=#{ balcony }
      and store_room=#{ store_room }
      and loft=#{ loft }
      and bedroom=#{ bedroom }
      and restroom=#{ restroom }
      and garden=#{ garden }
      and kitchen=#{ kitchen }
      and house_type=#{ house_type }
      and remark=#{ remark }
      and tb_house_id=#{ tb_house_id }

select id,drawing_room,balcony,store_room,loft,bedroom,restroom,garden,kitchen,house_type,remark,tb_house_id from tb_house_type

      and id=#{ id }
      and drawing_room=#{ drawing_room }
      and balcony=#{ balcony }
      and store_room=#{ store_room }
      and loft=#{ loft }
      and bedroom=#{ bedroom }
      and restroom=#{ restroom }
      and garden=#{ garden }
      and kitchen=#{ kitchen }
      and house_type=#{ house_type }
      and remark=#{ remark }
      and tb_house_id=#{ tb_house_id }

limit #{offset},#{rows}

select id,drawing_room,balcony,store_room,loft,bedroom,restroom,garden,kitchen,house_type,remark,tb_house_id from tb_house_type

      and id=#{ id }
      and drawing_room=#{ drawing_room }
