mapper.delete:
delete from tb_receipt

 id=#{id}
      and date=#{ date }
      and method=#{ method }
      and amount=#{ amount }
      and client_id=#{ client_id }
      and payee=#{ payee }
      and manager=#{ manager }

mapper.update:
update tb_receipt

      id=#{ id },
      date=#{ date },
      method=#{ method },
      amount=#{ amount },
      client_id=#{ client_id },
      payee=#{ payee },
      manager=#{ manager },


 id=#{id}
      and date=#{ date }
      and method=#{ method }
      and amount=#{ amount }
      and client_id=#{ client_id }
      and payee=#{ payee }
      and manager=#{ manager }

mapper.select:
select id,date,method,amount,client_id,payee,manager from tb_receipt

      and id=#{ id }
      and date=#{ date }
      and method=#{ method }
      and amount=#{ amount }
      and client_id=#{ client_id }
      and payee=#{ payee }
      and manager=#{ manager }

select id,date,method,amount,client_id,payee,manager from tb_receipt

      and id=#{ id }
      and date=#{ date }
      and method=#{ method }
      and amount=#{ amount }
      and client_id=#{ client_id }
      and payee=#{ payee }
      and manager=#{ manager }

limit #{offset},#{rows}

select id,date,method,amount,client_id,payee,manager from tb_receipt

      and id=#{ id }
      and date=#{ date }
      and method=#{ method }
      and amount=#{ amount }
      and client_id=#{ client_id }
      and payee=#{ payee }
      and manager=#{ manager }

limit 0,1

select count(1) from tb_receipt

      and id = #{ id }
      and date = #{ date }
      and method = #{ method }
      and amount = #{ amount }
      and client_id = #{ client_id }
      and payee = #{ payee }
      and manager = #{ manager }
