mapper.delete:
delete from tb_schedule

 id=#{id}
      and book_time=#{ book_time }
      and client_id=#{ client_id }
      and house_id=#{ house_id }
      and payed_money=#{ payed_money }

mapper.update:
update tb_schedule

      id=#{ id },
      book_time=#{ book_time },
      client_id=#{ client_id },
      house_id=#{ house_id },
      payed_money=#{ payed_money },


 id=#{id}
      and book_time=#{ book_time }
      and client_id=#{ client_id }
      and house_id=#{ house_id }
      and payed_money=#{ payed_money }

mapper.select:
   select ts.id,
   ts.book_time,ts.client_id,ts.house_id,ts.payed_money,tc.name as client_name from tb_schedule ts
   left join tb_customer tc on ts.client_id=tc.id

		and ts.id=#{ id }
		and ts.book_time=#{ book_time }
		and ts.client_id=#{ client_id }
		and ts.house_id=#{ house_id }
		and ts.payed_money=#{ payed_money }

select id,book_time,client_id,house_id,payed_money from tb_schedule

      and id=#{ id }
      and book_time=#{ book_time }
      and client_id=#{ client_id }
      and house_id=#{ house_id }
      and payed_money=#{ payed_money }

limit #{offset},#{rows}

select id,book_time,client_id,house_id,payed_money from tb_schedule

      and id=#{ id }
      and book_time=#{ book_time }
      and client_id=#{ client_id }
      and house_id=#{ house_id }
      and payed_money=#{ payed_money }

limit 0,1

select count(1) from tb_schedule

      and id = #{ id }
      and book_time = #{ book_time }
      and client_id = #{ client_id }
      and house_id = #{ house_id }
      and payed_money = #{ payed_money }
